class MarvelService {
   getResource = async (url) => {
       let res = await fetch(url);
       if (!res.ok) {
   throw new Error(`Could not fetch ${url}, status ${res.status}`);
       }
       return await res.json();
   }

   getAllCharacters = () => {
       return this.getResource('https://gateway.marvel.com:443/v1/public/characters?series=9&offset=210&apikey=35184d6c2169fa4afb43d4ec486bd3cb\n');
   }
   getCharacter = (id) => {
       return this.getResource(`https://gateway.marvel.com:443/v1/public/characters${id}?series=9&offset=210&apikey=35184d6c2169fa4afb43d4ec486bd3cb\n`)
   }

}

export default MarvelService;
