import {useHttp} from "../hooks/http.hook";

const useMarvelService  = () => {
    _apiBase = 'https://gateway.marvel.com:443/v1/public/';
    _apiKey = '?apikey=35184d6c2169fa4afb43d4ec486bd3cb\n'
     _baseOffset = 210;





    getAllCharacters = async (offset = this._baseOffset) => {
        const res = await this.getResource(`https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=${offset}&apikey=35184d6c2169fa4afb43d4ec486bd3cb`);
        return res.data.results.map(this._transformCharacter)
        // const res = await this.getResource(`${this._apiBase}characters${this._apiKey}`);
    }
    getCharacter = async (id) => {
        const res = await this.getResource(`${this._apiBase}characters/${id}${this._apiKey}`);
        return this._transformCharacter(res.data.results[0]);
    }

    _transformCharacter = (char) => {
        return {
            id: char.id,
            name: char.name,
            description: char.description ? `${char.description.slice(0,210)}...` : 'Sorry, description has been lost somewhere in the UNIVERSE',
            thumbnail: char.thumbnail.path + '.' + char.thumbnail.extension,
            homepage: char.urls[0].url,
            wiki: char.urls[1].url,
            comics: char.comics.items
        }
    }

 }

 export default MarvelService;
