class MarvelService {
   _apiBase = 'https://gateway.marvel.com:443/v1/public/';
   _apiKey = '?apikey=35184d6c2169fa4afb43d4ec486bd3cb\n'

   getResource = async (url) => {
       let res = await fetch(url);
       if (!res.ok) {
   throw new Error(`Could not fetch ${url}, status ${res.status}`);
       }
       return await res.json();
   }

   getAllCharacters = () => {
       return this.getResource(`${this._apiBase}characters?apikey=35184d6c2169fa4afb43d4ec486bd3cb\n`);
   }
   getCharacter = (id) => {
       return this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}`)
   }

}

export default MarvelService;
