import './comicsList.scss';
import uw from '../../resources/img/UW.png';
import xMen from '../../resources/img/x-men.png';
import useMarvelService from "../../services/MarvelService";
import {useEffect, useState} from "react";



const ComicsList = () => {
    const {loading,error,cleanError,getAllComics} = useMarvelService();

    const [comicList, setComicList] = useState([]);

    useEffect(() => {
        onRequest()
    }, []);


    const onRequest = (offset,initial) => {
        getAllComics(offset)
            .then(onComicListLoaded)
    }

    const onComicListLoaded = (fetchData) => {
        setComicList(comicList => comicList.concat(fetchData));
    }

    const renderComics = (arr) => {
        const {id,title,price,thumbnail,link} = arr

        const comics = arr.map((item,i) => {
            return (
                <li className="comics__item" key={id}>
                    <a href={link}>
                        <img src={thumbnail} alt="ultimate war" className="comics__item-img"/>
                        <div className="comics__item-name">{title}</div>
                        <div className="comics__item-price">{price}</div>
                    </a>
                </li>
            )
        })
        return (
            <ul className="comics__grid">
                {comics}
            </ul>
        )
    }

    const items = renderComics(comicList);

    return (
        <div className="comics__list">
            {items}
        <button className="button button__main button__long">
                <div className="inner">load more</div>
            </button>
        </div>
    )
}

export default ComicsList;
